    private String getFile()          // Function to get file to read
    {
        JFileChooser chooser = new JFileChooser();  // The instance of a file chooser
        FileNameExtensionFilter filter = new FileNameExtensionFilter(   // Filter
                "txt Files", "txt");
        chooser.setFileFilter(filter);
        int returnVal = chooser.showOpenDialog(null);
        if(returnVal == JFileChooser.APPROVE_OPTION) {
           return chooser.getSelectedFile().getAbsolutePath();
        }else{
            return null;
        }
    }


